//Zigbee发送
void Send_ZigbeeData_To_Fifo(uint8_t* p, uint8_t len)
{
	if (len <= 8)ExtSRAMInterface.ExMem_Write_Bytes(0x6008, p, len);
	else if (8 < len <= 16)ExtSRAMInterface.ExMem_Write_Bytes(0x6080, p, len);
	else if (len > 16)ExtSRAMInterface.ExMem_Write_Bytes(0x6180, p, len);
	delay(200);
}

void Zigbee_Send_degree(uint8_t* buf, uint8_t number, uint16_t ms)
{

	Serial.print(" ");
	for (int i = 0; i < number; i++)
	{
		ExtSRAMInterface.ExMem_Write_Bytes(0x6008, buf, 8);
		delay(ms);
	}
}

void Printf_HEX_Info(uint8_t* p, uint8_t len)
{
	for (int i = 0; i < len; i++)
	{
		Serial.print(ExtSRAMInterface.ExMem_Read(p + i), HEX);
		Serial.print(" ");
	}
	Serial.println(" ");
}

/**********************************************************************
 * 函 数 名 ：  红外发送控制次数默认红外数据为6位
 * 参    数 ：  *s    	红外数组
 * 返 回 值 ：  无
* 简    例 ：  Infrared_Send_degree(s,n);			s:红外数组默认6位  n红外发送的次数
**********************************************************************/
void Infrared_Send_degree(uint8_t* s, uint8_t number, uint16_t ms)			//红外发送次数函数，默认数组长度为6位
{
	for (int i = 0; i < number; i++)
	{
		delay(ms);						//延时状态已经差不多调试到最佳状态，不要随便修改
		Infrare.Transmition(s, 6);				//默认长度为6位
		delay(ms);
	}
}
