void Amendment_Front()
{
	//u16 Lef = 0,Rig = 0;
//	u16 white_time_Flag = 0;
//	u32 white_time = 0;
	u32 all_Error_times = gt_get() + 5000;
	if (H8_HEX() == 0x00 || Q7_HEX() == 0x00)
		Car_Go(25, 150);
	while (1)
	{
		if (gt_get_sub(all_Error_times) == 0)
			break;
		//========================================================================================
		if (H8_HEX() == 0xfb || H8_HEX() == 0xf9								//1111 1011   -   1111 1001
			|| H8_HEX() == 0xfd || H8_HEX() == 0xfc								//1111 1101   -   1111 1100
			|| H8_HEX() == 0xfe)															//1111 1110
		{
			Car_RorL(R, 90, 40);		//右转任意角度
		}
		else if (H8_HEX() == 0x7f || H8_HEX() == 0x3f						//0111 1111   -   0011 1111
			|| H8_HEX() == 0xbf || H8_HEX() == 0x9f						//1011 1111   -   1001 1111
			|| H8_HEX() == 0xdf)													//1101 1111
		{
			Car_RorL(L, 93, 40);		//左转任意角度	*
		}
		else
		{
			if (Q7_HEX() == 0x3f || Q7_HEX() == 0x1f || Q7_HEX() == 0x5f)
			{
				Car_RorL(L, 93, 50);		//左转任意角度	*
			}
			else if (Q7_HEX() == 0x7e || Q7_HEX() == 0x7c || Q7_HEX() == 0x7d)
			{
				Car_RorL(R, 90, 50);		//右转任意角度	*
			}
			else
			{
				break;
			}
		}
	}
}

void Amendment_Front_2()
{
	uint8_t gd_buf, H4_buf, L4_buf;
	uint8_t H4_light = 0, L4_light = 0;

	uint8_t Requirement_1 = 0;
	uint8_t Requirement_2 = 0;

	uint32_t all_Error_times = millis();//gt_get() + 1000;

	gd_buf = ExtSRAMInterface.ExMem_Read(TRACK_ADDR);

	H4_buf = ((gd_buf & 0xF0) >> 4);
	L4_buf = gd_buf & 0x0F;
	for (uint8_t i = 0x01; i < 0x10; i <<= 1)
	{
		if (i & H4_buf)
		{
			H4_light++;
		}
		if (i & L4_buf)
		{
			L4_light++;
		}
	}
	//Send_InfoData_To_Fifo("H4",2);
	//Num_Debug_Info(H4_light);
	//Send_InfoData_To_Fifo("L4",2);
	//Num_Debug_Info(L4_light);
	if (H4_light > L4_light)
	{
		//Send_InfoData_To_Fifo(">",1);
		//Send_UpMotor(70, -65);		//右转
		//DCMotor.SpeedCtr(80, -65);
		DCMotor.SpeedCtr(70, -65);
	}
	else if (H4_light < L4_light)
	{
		//Send_InfoData_To_Fifo("<",1);
		//Send_UpMotor(-65, 70);		//左转
		DCMotor.SpeedCtr(-65, 70);
	}

	while (1)
	{
		//gd_buf = Get_Host_UpTrack(TRACK_H8);
		gd_buf = ExtSRAMInterface.ExMem_Read(TRACK_ADDR);
		if (gd_buf == 0xE7)
		{
			DCMotor.SpeedCtr(0, 0);
			break;
		}
		if ((gd_buf & 0x10) == 0 && Requirement_1 == 0)		//00010000
		{
			Requirement_1 = 1;
		}
		if ((gd_buf & 0x08) == 0 && Requirement_2 == 0)		//00001000
		{
			Requirement_2 = 1;
		}
		if (Requirement_1 == 1 && Requirement_2 == 1)
		{
			DCMotor.SpeedCtr(0, 0);
			break;
		}
		//if (gt_get_sub(all_Error_times) == 0)
		//	break;
		if (millis() - all_Error_times > 1000)
		{
			DCMotor.SpeedCtr(0, 0);
			break;
		}
	}
}


