/*****************************************************************
 * 函 数 名 ：	自动调节光照强度函数
 * 参    数 ：	gear -> 目标挡位值
 * 返 回 值 ：	gear_init -> 初始挡位值
 * 简     例：	gear_init = Light_Inf(3);	// 智能路灯调至3档
*****************************************************************/
uint8_t Light_Inf_1(uint8_t gear)
{
	uint8_t i;
	uint8_t gear_init = 0;	
	uint16_t array[2];		
	if (gear == 0 || gear > 4)
	{
		gear = gear % 4 + 1;
	}
	light_goal = gear;
	ali_ldZ = light_goal;
	Serial.print("mubiao:");
	Serial.println(light_goal);
	delay_ms(500);
	delay_ms(500);
	array[0] = BH1750.ReadLightLevel();		
	for (i = 0; i < 4; i++)
	{
		gear_init++;
		delay_ms(200);
		Infrare.Transmition(Command.HW_Dimming1, 4);
		delay_ms(500);
		delay_ms(500);
		delay_ms(500);
		delay_ms(500);

		array[1] = BH1750.ReadLightLevel();			
		delay_ms(500);
		if (array[0] < array[1])
		{
			array[0] = array[1];
			array[1] = 0;
		}
		else
		{
			gear_init = 5 - gear_init;
			break;
		}
	}
	if (gear == 2)
	{
		Infrare.Transmition(Command.HW_Dimming1, 4);	
	}
	else if (gear == 3)
	{
		Infrare.Transmition(Command.HW_Dimming2, 4);
	}
	else if (gear == 4)
	{
		Infrare.Transmition(Command.HW_Dimming3, 4);	
	}

	Start_Light_dear = gear_init;
	ali_ldC = Start_Light_dear;
	Serial.print("chushi:");
	Serial.println(Start_Light_dear);
	
	return gear_init;
}

