/*****************************************
* 函数：A、B车库到达指定层数(运行并等待)
* 变量：A_Floor		A车库目标层数
*		B_Floor		B车库目标层数
*****************************************/
void Garage_AB_Zigbee(uint8_t A_Floor, uint8_t B_Floor)
{
	uint8_t Ensure_A = 0, Ensure_B = 0;
	Break_time = millis();
	tim_a = 150;
	while (1)
	{
		tim_a++;
		delay(10);
		if (!(tim_a % 80))
		{
			if (Ensure_B == 0)
			{
				delay(200);
				Send_ZigbeeData_To_Fifo(Drive.GarageB_Get_Floor, 8);
			}
			if (Ensure_A == 0)
			{
				delay(200);
				Send_ZigbeeData_To_Fifo(Drive.GarageA_Get_Floor, 8);	
			}
		}
		if (tim_a > 200)
		{
			tim_a = 0;
			if (Ensure_A == 0)
			{
				delay(200);
				if (A_Floor == 1)
				{
					Send_ZigbeeData_To_Fifo(Drive.GarageA_To1, 8);
				}
				else if (A_Floor == 2)
				{
					Send_ZigbeeData_To_Fifo(Drive.GarageA_To2, 8);
				}
				else if (A_Floor == 3)
				{
					Send_ZigbeeData_To_Fifo(Drive.GarageA_To3, 8);	
				}
				else if (A_Floor == 4)
				{
					Send_ZigbeeData_To_Fifo(Drive.GarageA_To4, 8);	
				}
				else
				{
					Send_ZigbeeData_To_Fifo(Drive.GarageA_To1, 8);	
				}
			}
			if (Ensure_B == 0)
			{
				delay(200);
				if (B_Floor == 1)
				{
					Send_ZigbeeData_To_Fifo(Drive.GarageB_To1, 8);	
				}
				else if (B_Floor == 2)
				{
					Send_ZigbeeData_To_Fifo(Drive.GarageB_To2, 8);	
				}
				else if (B_Floor == 3)
				{
					Send_ZigbeeData_To_Fifo(Drive.GarageB_To3, 8);	
				}
				else if (B_Floor == 4)
				{
					Send_ZigbeeData_To_Fifo(Drive.GarageB_To4, 8);	
				}
				else
				{
					Send_ZigbeeData_To_Fifo(Drive.GarageB_To1, 8);  
				}
			}
		}
		if (ExtSRAMInterface.ExMem_Read(0x6100) != 0x00)	 
		{
			ExtSRAMInterface.ExMem_Read_Bytes(ZigBee_command, 8);
			Printf_HEX_Info(ZigBee_command, 8);
			if ((ZigBee_command[0] == 0x55) && (ZigBee_command[1] == 0x0D) && Ensure_A == 0)
			{
				if ((ZigBee_command[2] == 0x03) && (ZigBee_command[3] == 0x01))
				{
					if (ZigBee_command[4] == A_Floor)
					{
						Serial.print("A");
						Ensure_A = 1;
					}
				}
			}
			if ((ZigBee_command[0] == 0x55) && (ZigBee_command[1] == 0x05) && Ensure_B == 0)
			{
				if ((ZigBee_command[2] == 0x03) && (ZigBee_command[3] == 0x01))
				{
					if (ZigBee_command[4] == B_Floor)
					{
						Serial.print("B");
						Ensure_B = 1;
					}
				}
			}
			if (Ensure_A == 1 && Ensure_B == 1)
			{
				break;
			}
		}
		if ((millis() - Break_time) >= 60000)
		{
			Cba_Beep(50, 50, 5);			
			break;
		}
	}
}

