void TerrainBack_Zigbee(uint8_t number)//获取特殊地形
{
	tim_a = 600;
	tim_b = 0;
	WaitTerrain_Flag = 0;
	delay_ms(300);
	Zigbee[0] = 0x55;
	Zigbee[1] = 0x10;
	Zigbee[2] = 0x10;
	Zigbee[3] = 0x01;
	Zigbee[4] = 0x00;
	Zigbee[5] = 0x00;
	Zigbee[6] = (Zigbee[2] + Zigbee[3] + Zigbee[4] + Zigbee[5]) % 256;
	Zigbee[7] = 0xBB;
	delay_ms(100);
	while (WaitTerrain_Flag != 0xBA && WaitTerrain_Flag != 0xAB)
	{
		delay_ms(1);
		tim_a++;
		if (tim_a >= 500)		//大于500ms
		{
			tim_a = 0;
			tim_b++;
			if (tim_b >= 5)
			{
				Serial.print("Break");
				WaitTerrain_Flag = 0xAB;
				break;
			}
			Zigbee_Send_degree(Zigbee, number, 300);
		}
		if (ExtSRAMInterface.ExMem_Read(0x6100) != 0x00)              //从车接收ZigBee数据
		{
			ExtSRAMInterface.ExMem_Read_Bytes(ZigBee_command, 8);

			if ((ZigBee_command[0] == 0x55) && (ZigBee_command[1] == 0x10))
			{

				if ((ZigBee_command[2] == 0x10) && (ZigBee_command[3] == 0x01))
				{
					if (ZigBee_command[4] == 0x32)
					{
						Serial.print("0xBA");
						Serial2.write(0x06);
						WaitTerrain_Flag = 0xBA;

					}
					else if (ZigBee_command[4] == 0x31)
					{
						Serial.print("0xAB");
						Serial2.write(0x07);
						WaitTerrain_Flag = 0xAB;

					}
					else
					{
						Serial.print("error");
						Cba_Beep(50, 50, 3);
						WaitTerrain_Flag = 0xAB;
					}
					break;
				}
			}
		}
	}
}

