/*根据两个方向计算相差的度数
*  +表示用该向右转多少 -表示应该向左转多少
*/
int fxd(char fxcar, char fxrw) {
	int i = 0;

	char c[] = "NBECSDWA";
	int len = 8;
	while (c[i] != fxcar)
	{
		i++;
	}
	//向右长度
	int j = i;
	int right = 0;
	while (c[j] != fxrw)
	{
		j = (j + 1) % 8;
		right++;
	}
	int k = i;
	int left = 0;
	while (c[k] != fxrw)
	{
		if (k == 0)
		{
			k = 7;
		}
		else {
			k--;
		}
		left++;

	}
	int l = 0;
	if (right < left) {
		l = right;
	}
	else {
		l = left;
		l *= -1;
	}
	return l;

}

